mapper.select:
SELECT country, brand, partnum, description, msrp net
FROM vparts_all
WHERE partnum=#{partnum}

SELECT partnum, country, net, qty, memo 
FROM cart
WHERE id=#{id}

mapper.update:
UPDATE cart
SET qty = #{qty}, memo=#{memo}
WHERE partnum=#{partnum} AND id=#{id}

{call declare begin
MERGE INTO cart USING dual ON (partnum = #{item.part} AND id=#{item.id}) WHEN MATCHED THEN UPDATE SET qty = qty+#{item.qty}, memo = #{item.memo} WHERE partnum=#{item.part} AND id=#{item.id} WHEN NOT MATCHED THEN INSERT (memo, partnum, qty, id, ponum, no, net, country) VALUES ( #{item.memo}, #{item.part}, #{item.qty}, #{item.id}, '22', (SELECT MAX(no)+1 FROM cart), (SELECT msrp net FROM vparts_all WHERE partnum = #{item.part}), (SELECT country FROM vparts_all WHERE partnum = #{item.part}) );
end }

mapper.select:
SELECT net, country, id, memo, partnum, qty, b_smallname
FROM cart
WHERE id=#{id}
