mapper.update:
UPDATE approval
SET app_date = SYSDATE
WHERE num = #{num} AND seq = #{seq}

mapper.select:
	SELECT num,title,content,appline,coopline,empno,ollno,state FROM (
    	SELECT A.*, 
                 ROWNUM AS RNUM,
                 TRUNC((ROWNUM-1)/10+1) AS PAGE,
                 COUNT(*) OVER() AS TOTCNT FROM (
			SELECT num,title,content,appline,coopline,empno,ollno,state
			FROM appcontent
			WHERE state = #{state}
			ORDER BY num DESC
    	) A
	) WHERE PAGE = #{page}

SELECT TRUNC((count(*)-1)/10+1)
FROM appcontent
WHERE state = #{state}

	SELECT num,title,content,appline,coopline,empno,ollno,state FROM (
    	SELECT A.*, 
                 ROWNUM AS RNUM,
                 TRUNC((ROWNUM-1)/10+1) AS PAGE,
                 COUNT(*) OVER() AS TOTCNT FROM (
			SELECT num,title,content,appline,coopline,empno,ollno,state
			FROM appcontent
			WHERE empno = #{empno} AND state = #{state}
			ORDER BY num DESC
    	) A
	) WHERE PAGE = #{page}

	SELECT num,seq,empno,app_date FROM (
    	SELECT A.*, 
                 ROWNUM AS RNUM,
                 TRUNC((ROWNUM-1)/10+1) AS PAGE,
                 COUNT(*) OVER() AS TOTCNT FROM (
			SELECT ac.*
			FROM appcontent ac, (
				SELECT b.* 
				FROM approval b, (
					SELECT num,min(seq) seq 
					FROM approval 
					WHERE app_date is null group by num
					) c
				WHERE b.num = c.num AND b.seq = c.seq AND b.empno = #{empno}
			) ap
			WHERE ac.appline = ap.num AND ac.state = #{state}
			ORDER BY ac.num DESC;
    	) A
	) WHERE PAGE = #{page}

SELECT TRUNC((count(*)-1)/10+1)
FROM (SELECT ac.*
		FROM appcontent ac, (
			SELECT b.* 
			FROM approval b, (
				SELECT num,min(seq) seq 
				FROM approval 
				WHERE app_date is null group by num
				) c
			WHERE b.num = c.num AND b.seq = c.seq AND b.empno = #{empno}
		) ap
		WHERE ac.appline = ap.num AND ac.state = #{state}
	)

SELECT TRUNC((count(*)-1)/10+1)
FROM appcontent
WHERE empno = #{empno} AND state = #{state}
